package JavaClasses;import java.io.Serializable;import java.util.UUID;public class Person implements Serializable{    private String name;    private Integer height;    private String passportID;    private Location location;    public Person(String name, Integer height, Location location){        this.name = name;        this.height = height;        this.passportID = UUID.randomUUID().toString();        this.location = location;    }    public Person(String name, Integer height, String passportId, Location location){        this.name = name;        this.height = height;        this.passportID = passportId;        this.location = location;    }    public String getName(){        return name;    }    public Integer getHeight(){        return height;    }    public String getPassportID(){        return passportID;    }    public Location getLocation(){        return location;    }    @Override    public String toString(){        return "\nИмя : " + name +                "\nРост : "+ height +                "\nID паспорта : " + passportID +                "\nМестоположение : " + location;    }    }